/* eslint-disable prettier/prettier */

import { View, Text, SafeAreaView, ScrollView, Keyboard, Alert } from 'react-native';
import React from 'react';
import AsyncStorage from '@react-native-async-storage/async-storage';
import Colors from '../../const/colors';
import Input from '../components/input';
import Button from '../components/Button';
import Loader from '../components/Loader';

const RegistrationScreen = ({navigation})=>{
    const [inputs, setInputs] = React.useState({
        email: '',
        fullname: '',
        phone: '',
        password: '',
});

const [errors,setErrors] = React.useState({});
const [loading, setLoading] = React.useState(false)
const validate = () => {
        Keyboard.dismiss();
        
    let isValid = true;

if (!inputs.email) {
        handleError('Please input email', 'email');
        let isValid = false;
    }
    else if (!inputs.email.match(/\S+@\S+\.\S+/)) {
        handleError('Please input a valid email', 'email');
        isValid = false;
    }
if (!inputs.fullname) {
      handleError('Please input fullname', 'fullname');
      isValid = false;
    }

if (!inputs.phone) {
      handleError('Please input phone number', 'phone');
      isValid = false;
    }
else if (inputs.phone.length < 10) {
    handleError('Minimun phone length of 10', 'phone');
    isValid = false;
    }

if (!inputs.password) {
      handleError('Please input password', 'password');
      isValid = false;
    } else if (inputs.password.length < 5) {
      handleError('Minimum password length of 5', 'password');
      isValid = false;
    }

if (isValid) {
      register();
    }
    };
    const register = () => {
        setLoading(true);
        setTimeout(() => {
        try {
            AsyncStorage.setItem('userData', JSON.stringify(inputs));
            setInputs({
                email: '',
                fullname: '',
                phone: '',
                password: '',
            });
            setLoading(false);
            navigation.navigate('LoginScreen');
            } catch (error) {
                Alert.alert('Error', 'Something went wrong');
            }
        }, 2000);
    }
const handleOnChange = (text,input)=>{
    setInputs(prevState => ({...prevState, [input]:text }));
};
const handleError = (error, input) => {
        setErrors(prevState => ({ ...prevState, [input]: error }));
    };
return (
<SafeAreaView style={{backgroundColor: Colors.white, flex:1}}>
<Loader visible={loading}/>
<ScrollView contentContainerStyle={{
    paddingTop:50,
    paddingHorizontal:20,
}}>
<Text style={{color:Colors.black,fontSize:40,fontWeight:'bold'}}>Register</Text>
<Text style={{color:Colors.black,fontSize:18,margin:10,marginRight:10}}>Enter your Details to Register</Text>
<View style={{marginVertical:20}}>
    <Input
    placeholder="Enter your email address"
    image="data:image/png;base64,"
    label="Email"
    onFocus={() => handleError(null, 'email')}
    onChangeText={(text)=>handleOnChange(text,'email')}
    error={errors.email}
    value={inputs.email}
/>

<Input
    placeholder="Enter your full name"
    image="https://e7.pngegg.com/pngimages/103/590/png-clipart-computer-icons-user-profile-avatar-heroes-monochrome-thumbnail.png"
    label="Full Name"
    onChangeText={(text) => handleOnChange(text, 'fullname')}
    onFocus={() => handleError(null, 'fullname')}
    error={errors.fullname}
                    value={inputs.fullname}

    />
<Input
    keyboardType="numeric"
    placeholder="Enter your phone number"
    image="https://e7.pngegg.com/pngimages/279/613/png-clipart-phone-phone.png"
    label="Phone Number"
    onChangeText={(text) => handleOnChange(text, 'phone')}
    onFocus={() => handleError(null, 'phone')}
    error={errors.phone}
                    value={inputs.phone}

/>
<Input
    placeholder="Enter your password"
    image="data:image/png;base64,"
    label="Password"
    password
    onChangeText={(text) => handleOnChange(text, 'password')}
    onFocus={() => handleError(null, 'password')}
    error={errors.password}

                    value={inputs.password}

    />
<Button title="Register" onPress={validate}/>
<Text
    onPress={() => navigation.navigate('LoginScreen')}
    style={{
        textAlign:'center',
        color:Colors.black,
        fontSize:16,
        fontWeight:'bold'}}>
        Already have an account ?Login
</Text>
</View>
</ScrollView>
    </SafeAreaView>
    );
};

export default RegistrationScreen;
